import React, { useEffect, useState } from 'react';
import { getAllMeals } from '../Services/mealtService'; // Ajusta la ubicación según la estructura de tu proyecto

const MealPreviwGrid = () => {
  const [meals, setMeals] = useState([]);

  useEffect(() => {
    // Llama a la función para obtener todas las comidas cuando el componente se monta
    getAllMeals()
      .then((mealsData) => {
        setMeals(mealsData);
      })
      .catch((error) => {
        console.error("Error al obtener comidas:", error);
      });
  }, []);

  return (
    <div className='meal-grid'>
      {meals.map((meal) => (
        <div key={meal.idMeal} className='meal-preview'>
          <img src={meal.strMealThumb} alt={meal.strMeal} />
          <h2>{meal.strMeal}</h2>
          {/* Otros detalles de la comida que desees mostrar */}
        </div>
      ))}
    </div>
  );
};

export default MealPreviwGrid;
