import React, { useState } from "react";
import { useAppContext } from "../../AppContext"; // Importa el contexto

import SearchIcon from "./searchIcon";

import "./index.css";

const SearchBox = () => {
  const { meals, setMeals } = useAppContext(); // Obtiene meals y setMeals del contexto
  const [searchTerm, setSearchTerm] = useState(""); // Estado para el término de búsqueda

  const handleSearch = async () => {
    try {
      const response = await fetch(
        `https://www.themealdb.com/api/json/v1/1/search.php?s=${searchTerm}`
      );
      const data = await response.json();
      setMeals(data.meals || []); // Actualiza los resultados de la búsqueda en el estado meals
    } catch (error) {
      console.error("Error al buscar comidas:", error);
    }
  };

  return (
    <div className="search-box">
      <SearchIcon />
      <input
        type="text"
        placeholder="Busca en este sitio web"
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
      />
      <button onClick={handleSearch}>Buscar</button>
    </div>
  );
};

export default SearchBox;
