import React, { useEffect } from 'react';
import { useAppContext } from './AppContext';
import Navbar from './components/navbar';
import SearchBox from './components/searchBox';
import Home from './pages/home';

function App() {
  const { setMeals, searchMealByName } = useAppContext();

  useEffect(() => {
    // Cargar todas las comidas al iniciar la aplicación
    async function fetchAllMeals() {
      try {
        const data = await searchMealByName('');
        setMeals(data || []);
      } catch (error) {
        console.error('Error al cargar todas las comidas:', error);
      }
    }

    fetchAllMeals();
  }, [searchMealByName, setMeals]);

  return (
    <div className='App'>
      <Navbar>
        <SearchBox />
      </Navbar>
      <Home />
    </div>
  );
}

export default App;
