import React, { useEffect, useState } from 'react';
import './index.css';
import { SearchMealByName } from '../../Service/mealtService';

const MealPreviwGrid = () => {
  const [meals, setMeals] = useState([]); // Estado para almacenar las comidas

  useEffect(() => {
    // Llama a la función para buscar comidas cuando se monta el componente
    async function fetchMeals() {
      try {
        const data = await SearchMealByName('query'); // Reemplaza 'query' con el término de búsqueda real
        setMeals(data);
      } catch (error) {
        console.error('Error al buscar comidas:', error);
      }
    }

    fetchMeals();
  }, []);

  return (
    <div className='meal-grid'>
      {meals.map((meal) => (
        <div key={meal.idMeal} className='meal-item'>
          <img src={meal.strMealThumb} alt={meal.strMeal} />
          <h3>{meal.strMeal}</h3>
        </div>
      ))}
    </div>
  );
};

export default MealPreviwGrid;
