import React, { createContext, useContext, useState, useEffect } from 'react';

const AppContext = createContext();

export const AppProvider = ({ children }) => {
  const [meals, setMeals] = useState([]);

  useEffect(() => {
    // Realiza la búsqueda de todas las comidas al montar la aplicación
    async function fetchMeals() {
      try {
        const response = await fetch('https://www.themealdb.com/api/json/v1/1/search.php?s=');
        const data = await response.json();
        setMeals(data.meals || []); // Si 'meals' es null o undefined, establece un array vacío
      } catch (error) {
        console.error('Error al buscar comidas:', error);
      }
    }

    fetchMeals();
  }, []);

  return (
    <AppContext.Provider value={{ meals }}>
      {children}
    </AppContext.Provider>
  );
};

export const useAppContext = () => {
  return useContext(AppContext);
};
