import React, { createContext, useContext, useState, useEffect } from 'react';
import { SearchMealByName } from './Service/mealtService';

const AppContext = createContext();

export const AppProvider = ({ children }) => {
  const [meals, setMeals] = useState([]);

  // Agrega la funci贸n SearchMealByName al contexto
  const searchMealByName = async (name) => {
    try {
      const data = await SearchMealByName(name);
      return data || [];
    } catch (error) {
      console.error('Error al buscar comidas:', error);
      return [];
    }
  };

  useEffect(() => {
    // Cargar todas las comidas al iniciar la aplicaci贸n
    async function fetchAllMeals() {
      try {
        const data = await SearchMealByName('');
        setMeals(data || []);
      } catch (error) {
        console.error('Error al cargar todas las comidas:', error);
      }
    }

    fetchAllMeals();
  }, []);

  return (
    <AppContext.Provider value={{ meals, setMeals, searchMealByName }}>
      {children}
    </AppContext.Provider>
  );
};

export const useAppContext = () => {
  return useContext(AppContext);
};
