import React, { useState } from "react";
import { useAppContext } from "../../AppContext";
import SearchIcon from "./searchIcon";
import "./index.css";

const SearchBox = () => {
  const { setMeals } = useAppContext();
  const [searchTerm, setSearchTerm] = useState("");

  const handleInputChange = (event) => {
    const newSearchTerm = event.target.value;
    setSearchTerm(newSearchTerm);

    if (newSearchTerm === "") {
      // Si el campo de búsqueda está vacío, muestra todas las comidas
      setMeals([]);
    } else {
      // Si hay texto en el campo de búsqueda, realiza la búsqueda por nombre y filtra
      performSearch(newSearchTerm);
    }
  };

  const performSearch = async (term) => {
    try {
      const response = await fetch(
        `https://www.themealdb.com/api/json/v1/1/search.php?s=${term}`
      );
      const data = await response.json();
      setMeals(data.meals || []);
    } catch (error) {
      console.error("Error al buscar comidas:", error);
    }
  };

  return (
    <div className="search-box">
      <SearchIcon />
      <input
        type="text"
        placeholder="Busca en este sitio web"
        value={searchTerm}
        onChange={handleInputChange}
      />
    </div>
  );
};

export default SearchBox;
