// AppContext.js
import React, { createContext, useContext, useState, useEffect } from 'react';
import { SearchMealByName } from './Service/mealService'; // Ajusta la ruta según tu estructura de carpetas

// Crea un contexto
const AppContext = createContext();

// Componente de contexto que proporciona el estado global y las funciones a los componentes secundarios
export const AppProvider = ({ children }) => {
  const [meals, setMeals] = useState([]);

  useEffect(() => {
    // Realiza la búsqueda de comidas al montar la aplicación
    async function fetchMeals() {
      try {
        const data = await SearchMealByName('query'); // Reemplaza 'query' con el término de búsqueda real
        setMeals(data);
      } catch (error) {
        console.error('Error al buscar comidas:', error);
      }
    }

    fetchMeals();
  }, []);

  return (
    <AppContext.Provider value={{ meals }}>
      {children}
    </AppContext.Provider>
  );
};

// Función personalizada para usar el contexto
export const useAppContext = () => {
  return useContext(AppContext);
};
