import React, { useState } from "react";
import { useAppContext } from "../../AppContext";
import SearchIcon from "./searchIcon";
import "./index.css";

const SearchBox = () => {
  const { meals, setMeals, searchMealByName } = useAppContext();
  
  // Establece un valor inicial con un espacio en blanco
  const [searchTerm, setSearchTerm] = useState(" ");

  const handleInputChange = async (event) => {
    const newSearchTerm = event.target.value;
    setSearchTerm(newSearchTerm);

    if (newSearchTerm.trim() === "") {
      // Si el campo de búsqueda está vacío o solo contiene espacios, muestra todas las comidas
      setMeals([]);
    } else {
      // Si hay texto en el campo de búsqueda, realiza la búsqueda por nombre y filtra
      try {
        const data = await searchMealByName(newSearchTerm);
        setMeals(data || []);
      } catch (error) {
        console.error("Error al buscar comidas:", error);
      }
    }
  };

  return (
    <div className="search-box">
      <SearchIcon />
      <input
        type="text"
        placeholder="Busca en este sitio web"
        value={searchTerm}
        onChange={handleInputChange}
      />
    </div>
  );
};

export default SearchBox;
