import React, { useState } from "react";
import { useAppContext } from "../../AppContext";
import SearchIcon from "./searchIcon";
import "./index.css";

const SearchBox = () => {
  const { meals, setMeals } = useAppContext();
  const [searchTerm, setSearchTerm] = useState("");

  const handleInputChange = (event) => {
    const newSearchTerm = event.target.value;
    setSearchTerm(newSearchTerm);

    // Si el campo de búsqueda está vacío, muestra todas las comidas
    if (newSearchTerm === "") {
      setMeals([]);
    } else {
      // Filtrar las comidas cuyos nombres comienzan con el término de búsqueda
      const filteredMeals = meals.filter((meal) =>
        meal.strMeal.toLowerCase().startsWith(newSearchTerm.toLowerCase())
      );
      setMeals(filteredMeals);
    }
  };

  return (
    <div className="search-box">
      <SearchIcon />
      <input
        type="text"
        placeholder="Busca en este sitio web"
        value={searchTerm}
        onChange={handleInputChange}
      />
    </div>
  );
};

export default SearchBox;
