import React, { useState } from "react";
import { useAppContext } from "../../AppContext";
import SearchIcon from "./searchIcon";
import "./index.css";

const SearchBox = () => {
  const { setMeals } = useAppContext();
  const [searchTerm, setSearchTerm] = useState("");

  const handleSearch = async () => {
    try {
      if (searchTerm === "") {
        // Si el campo de búsqueda está vacío, muestra todas las comidas
        const response = await fetch(
          "https://www.themealdb.com/api/json/v1/1/search.php?s="
        );
        const data = await response.json();
        setMeals(data.meals || []);
      } else {
        // Si hay texto en el campo de búsqueda, realiza la búsqueda por nombre y filtra
        const response = await fetch(
          "https://www.themealdb.com/api/json/v1/1/search.php?s="
        );
        const data = await response.json();

        // Filtrar las comidas cuyos nombres comiencen con el término de búsqueda
        const filteredMeals = data.meals.filter((meal) =>
          meal.strMeal.toLowerCase().startsWith(searchTerm.toLowerCase())
        );

        setMeals(filteredMeals || []);
      }
    } catch (error) {
      console.error("Error al buscar comidas:", error);
    }
  };

  const handleInputChange = (event) => {
    const newSearchTerm = event.target.value;
    setSearchTerm(newSearchTerm);
    handleSearch();
  };

  return (
    <div className="search-box">
      <SearchIcon />
      <input
        type="text"
        placeholder="Busca en este sitio web"
        value={searchTerm}
        onChange={handleInputChange}
      />
    </div>
  );
};

export default SearchBox;
